mapper.update:
    UPDATE tbl_cslist 
    SET cs_replycnt = cs_replycnt + #{amount}
	WHERE cs_seq = #{cs_seq}

	UPDATE tbl_cslist 
    SET cs_replycnt_admin = cs_replycnt_admin + #{amount}
    WHERE cs_seq = #{cs_seq}

   		UPDATE tbl_cslist
   		SET cs_readcnt = cs_readcnt + 1  
		WHERE cs_seq = #{cs_seq}
